import React, { Component } from 'react';
import TeamMember from './TeamMember';
import memberInfo from './memberInfo';

class Team extends Component {
  constructor() {
    super();
    this.state = {
        memberInfo :memberInfo,
        members: [],
    }
    
    this.clickEvent = this.clickEvent.bind(this)
  }


  clickEvent(){
        this.state.members.push(this.state.memberInfo[0])
        console.log(this.state.members);
        this.newMembers = this.state.members.map(member =>{
          return
              <TeamMember
              key={member.id}
              img={member.img}
              name={member.name}
              position={member.position}
              phone={member.phone}
              email={member.email}
              website={member.website}
          />
        })
  }



  render() {
    return (
        <div className="row">
            
            <button className="btn btn-primary btn-lg btn-block" onClick={this.clickEvent}>Click me!</button>        
            {this.newMembers}
        </div>
    );
  }
}

export default Team_click;
